import React from "react";
import HeroBgAnimation from "../HeroBgAnimation";
import {
  HeroContainer,
  HeroBg,
  HeroLeftContainer,
  Img,
  HeroRightContainer,
  HeroInnerContainer,
  TextLoop,
  Title,
  Span,
  SubTitle,
  SocialMediaIcons,
  SocialMediaIcon,
  ResumeButton,
} from "./HeroStyle";
import HeroImg from "../../images/HeroImage.jpg";
import Typewriter from "typewriter-effect";
import { Bio } from "../../data/constants";
import styled, { keyframes } from "styled-components";

const changeShape = keyframes`
  0% {
    border-radius: 50%;
  }
  50% {
    border-radius: 0;
  }
  100% {
    border-radius: 50%;
  }
`;

const AnimatedImg = styled(Img)`
  animation: ${changeShape} 5s linear infinite;
`;
const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const StyledTextLoop = styled(TextLoop)`
  font-size: 28px;
  font-weight: 600;
  color: #4a4a4a;
  animation: ${fadeIn} 2s ease-in-out;
  /* Add more styles as needed */
`;

const StyledSpan = styled.span`
  color: #6c63ff; // Example of a more vibrant color
  margin-left: 10px;
  font-size: 28px;
  /* Add more styles as needed */
`;

const TypewriterWrapper = styled.div`
  display: inline;

  .Typewriter__cursor {
    font-size: 28px;
    color: #6c63ff; // Cursor color to match the text
    animation: blink 1s linear infinite;

    @keyframes blink {
      50% {
        opacity: 0;
      }
    }
  }
`;

const HeroSection = () => {
  return (
    <div id="about">
      <HeroContainer>
        <HeroBg>
          <HeroBgAnimation />
        </HeroBg>
        <HeroInnerContainer>
          <HeroLeftContainer id="Left">
            <Title>
              Hi, I am <br /> {Bio.name}
            </Title>
            <StyledTextLoop>
              A
              <StyledSpan>
                <TypewriterWrapper>
                  <Typewriter
                    options={{
                      strings: Bio.roles,
                      autoStart: true,
                      loop: true,
                    }}
                  />
                </TypewriterWrapper>
              </StyledSpan>
            </StyledTextLoop>
            <SubTitle>{Bio.description}</SubTitle>
            <ResumeButton href={Bio.resume} target="display">
              Check Resume
            </ResumeButton>
          </HeroLeftContainer>

          <HeroRightContainer id="Right">
            <AnimatedImg src={HeroImg} alt="hero-image" />
          </HeroRightContainer>
        </HeroInnerContainer>
      </HeroContainer>
    </div>
  );
};

export default HeroSection;
